function verificarWin() {
  if (win) {
    win_text.textContent = message;
    btn_restart.style.display = 'block';
    container.innerHTML = '';
    btn_player1.style.display = 'none';
    btn_player2.style.display = 'none';
  } else {
    if (game[0][0] == '@' && game[1][0] == '@' && game[2][0] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[0][0] == '@' && game[0][1] == '@' && game[0][2] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[0][0] == '@' && game[1][1] == '@' && game[2][2] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[0][1] == '@' && game[1][1] == '@' && game[2][1] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[0][2] == '@' && game[1][1] == '@' && game[2][0] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[1][0] == '@' && game[1][1] == '@' && game[1][2] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[2][0] == '@' && game[2][1] == '@' && game[2][2] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } else if (game[0][2] == '@' && game[1][2] == '@' && game[2][2] == '@') {
      message = 'Jugador 1 gano!';
      win = true;
    } //! Jugador 2
    else if (game[0][0] == '$' && game[1][0] == '$' && game[2][0] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[0][0] == '$' && game[0][1] == '$' && game[0][2] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[0][0] == '$' && game[1][1] == '$' && game[2][2] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[0][1] == '$' && game[1][1] == '$' && game[2][1] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[0][2] == '$' && game[1][1] == '$' && game[2][0] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[1][0] == '$' && game[1][1] == '$' && game[1][2] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[2][0] == '$' && game[2][1] == '$' && game[2][2] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } else if (game[0][2] == '$' && game[1][2] == '$' && game[2][2] == '$') {
      message = 'Jugador 2 gano!';
      win = true;
    } //! Empate
    else if (
      game[0][0] !== '#' &&
      game[0][1] !== '#' &&
      game[0][2] !== '#' &&
      game[1][0] !== '#' &&
      game[1][1] !== '#' &&
      game[1][2] !== '#' &&
      game[2][0] !== '#' &&
      game[2][1] !== '#' &&
      game[2][2] !== '#'
    ) {
      message = 'EMPATE!';
      win = true;
    }
  }
}
